
const axios = require('axios');

async function shortenUrl(originalUrl) {
  try {
    const response = await axios.post('https://srt.vyturex.com/shorten', {
      url: originalUrl
    });

    if (response.status === 200) {
      return response.data.shortUrl || response.data;
    } else {
      throw new Error(response.data.error || 'Error shortening URL');
    }
  } catch (error) {
    throw new Error(error.response.data.error || 'Failed to shorten URL');
  }
}

module.exports = {
  shorten: shortenUrl
};
